  id INTEGER PRIMARY KEY AUTOINCREMENT,
  
  -- âœ… Basic info (phÃ¹ há»£p vá»›i form hiá»‡n táº¡i)
  title TEXT NOT NULL,              -- TÃªn hiá»ƒn thá»‹
  slug TEXT UNIQUE NOT NULL,
  shortDesc TEXT,                   -- MÃ´ táº£ ngáº¯n
  desc TEXT,                        -- MÃ´ táº£ chi tiáº¿t (HTML/Markdown)
  
  -- âœ… Category
  category_slug TEXT,               -- Slug danh má»¥c
  
  -- âœ… SEO
  seo_title TEXT,
  seo_desc TEXT,
  keywords TEXT,                    -- JSON array: ["keyword1", "keyword2"]
  
  -- âœ… Extra content
  faq TEXT,                         -- JSON: [{Q: "...", A: "..."}]
  reviews TEXT,                     -- JSON: [{name: "A", rating: 5}]
  
  -- âœ… Media
  images TEXT,                      -- JSON array: ["url1", "url2"]
  video TEXT,
  
  -- Display settings
  status TEXT DEFAULT 'active' CHECK(status IN ('active', 'inactive', 'draft')),
  on_website INTEGER DEFAULT 1,    -- 1 = hiá»ƒn thá»‹, 0 = áº©n
  on_mini INTEGER DEFAULT 1,
  
  -- Timestamps
  created_at INTEGER NOT NULL,
  updated_at INTEGER NOT NULL
, sold INTEGER DEFAULT 0, rating REAL DEFAULT 5.0, rating_count INTEGER DEFAULT 0, stock INTEGER DEFAULT 0, brand TEXT DEFAULT '', tags TEXT DEFAULT '[]', search_text TEXT DEFAULT '', douyin_url TEXT);
CREATE TABLE variants (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  product_id INTEGER NOT NULL,
  
  -- Core info
  sku TEXT UNIQUE NOT NULL,
  name TEXT NOT NULL,              -- TÃªn variant: "Size M", "MÃ u Äá»"
  
  -- âœ… PRICING (PhÃ¹ há»£p vá»›i form hiá»‡n táº¡i)
  price REAL NOT NULL DEFAULT 0,          -- GiÃ¡ bÃ¡n thÆ°á»ng
  price_sale REAL,                        -- GiÃ¡ sale (giáº£m giÃ¡)
  price_wholesale REAL,                   -- GiÃ¡ sá»‰
  cost_price REAL,                        -- GiÃ¡ nháº­p (ná»™i bá»™)
  
  -- âœ… TIER PRICING (Silver, Gold, Diamond)
  price_silver REAL,
  price_gold REAL,
  price_diamond REAL,
  
  -- âœ… STOCK (Tá»“n kho master)
  stock INTEGER DEFAULT 0,
  
  -- Physical
  weight REAL DEFAULT 0,           -- gram
  
  -- Status
  status TEXT DEFAULT 'active' CHECK(status IN ('active', 'inactive', 'out_of_stock')),
  
  -- Media
  image TEXT,
  
  -- Timestamps
  created_at INTEGER NOT NULL,
  updated_at INTEGER NOT NULL,
  
  FOREIGN KEY (product_id) REFERENCES products(id) ON DELETE CASCADE
);
CREATE TABLE channel_products (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  
  channel TEXT NOT NULL CHECK(channel IN ('shopee', 'lazada', 'tiktok')),
  
  -- External IDs
  channel_item_id TEXT NOT NULL,
  channel_model_id TEXT,
  channel_sku TEXT,
  
  -- Internal mapping
  product_id INTEGER NOT NULL,
  variant_id INTEGER,
  
  -- âœ… GiÃ¡ riÃªng trÃªn sÃ n (Shopee/Lazada giÃ¡ khÃ¡c Website)
  channel_price REAL,
  channel_price_sale REAL,
  
  -- Sync status
  is_active INTEGER DEFAULT 1,
  last_sync_at INTEGER,
  
  created_at INTEGER NOT NULL,
  updated_at INTEGER NOT NULL,
  
  UNIQUE(channel, channel_item_id, channel_model_id),
  FOREIGN KEY (product_id) REFERENCES products(id) ON DELETE CASCADE,
  FOREIGN KEY (variant_id) REFERENCES variants(id) ON DELETE CASCADE
);
CREATE TABLE stock_logs (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  variant_id INTEGER NOT NULL,
  
  old_stock INTEGER NOT NULL,
  new_stock INTEGER NOT NULL,
  change INTEGER NOT NULL,
  
  reason TEXT NOT NULL CHECK(reason IN ('order', 'import', 'sync', 'adjustment', 'return')),
  channel TEXT,
  
  order_id INTEGER,
  notes TEXT,
  
  created_at INTEGER NOT NULL,
  
  FOREIGN KEY (variant_id) REFERENCES variants(id) ON DELETE CASCADE,
  FOREIGN KEY (order_id) REFERENCES orders(id) ON DELETE SET NULL
);
CREATE TABLE orders (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  
  order_number TEXT UNIQUE NOT NULL,
  channel TEXT NOT NULL CHECK(channel IN ('website', 'mini', 'shopee', 'lazada', 'tiktok')),
  channel_order_id TEXT,
  
  -- Customer
  customer_id INTEGER,
  customer_name TEXT,
  customer_phone TEXT,
  customer_email TEXT,
  
  -- Shipping
  shipping_name TEXT,
  shipping_phone TEXT,
  shipping_address TEXT,
  shipping_district TEXT,
  shipping_city TEXT,
  shipping_province TEXT,
  shipping_zipcode TEXT,
  
  -- Financial
  subtotal REAL NOT NULL DEFAULT 0,
  shipping_fee REAL DEFAULT 0,
  discount REAL DEFAULT 0,
  total REAL NOT NULL DEFAULT 0,
  
  -- Status
  status TEXT DEFAULT 'pending' CHECK(status IN ('pending', 'processing', 'shipped', 'completed', 'cancelled', 'returned')),
  payment_status TEXT DEFAULT 'pending' CHECK(payment_status IN ('pending', 'paid', 'refunded')),
  fulfillment_status TEXT DEFAULT 'unfulfilled' CHECK(fulfillment_status IN ('unfulfilled', 'fulfilled', 'partial')),
  
  payment_method TEXT,
  
  -- Notes
  customer_note TEXT,
  admin_note TEXT,
  
  created_at INTEGER NOT NULL,
  updated_at INTEGER NOT NULL, tracking_number TEXT, shipping_carrier TEXT, coin_used REAL DEFAULT 0, voucher_code TEXT, voucher_seller REAL DEFAULT 0, voucher_shopee REAL DEFAULT 0, commission_fee REAL DEFAULT 0, service_fee REAL DEFAULT 0, escrow_amount REAL DEFAULT 0, buyer_paid_amount REAL DEFAULT 0, estimated_shipping_fee REAL DEFAULT 0, actual_shipping_fee_confirmed REAL DEFAULT 0, seller_transaction_fee REAL DEFAULT 0, shop_id TEXT, shop_name TEXT,
  
  FOREIGN KEY (customer_id) REFERENCES customers(id) ON DELETE SET NULL
);
CREATE TABLE order_items (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  order_id INTEGER NOT NULL,
  
  product_id INTEGER,
  variant_id INTEGER,
  
  sku TEXT NOT NULL,
  name TEXT NOT NULL,
  variant_name TEXT,
  
  price REAL NOT NULL,
  quantity INTEGER NOT NULL DEFAULT 1,
  subtotal REAL NOT NULL,
  
  channel_item_id TEXT,
  channel_model_id TEXT, image TEXT,
  
  FOREIGN KEY (order_id) REFERENCES orders(id) ON DELETE CASCADE,
  FOREIGN KEY (product_id) REFERENCES products(id) ON DELETE SET NULL,
  FOREIGN KEY (variant_id) REFERENCES variants(id) ON DELETE SET NULL
);
CREATE TABLE customers (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  
  name TEXT NOT NULL,
  phone TEXT UNIQUE,
  email TEXT UNIQUE,
  
  tier TEXT DEFAULT 'regular' CHECK(tier IN ('regular', 'silver', 'gold', 'diamond')),
  
  total_orders INTEGER DEFAULT 0,
  total_spent REAL DEFAULT 0,
  
  created_at INTEGER NOT NULL,
  updated_at INTEGER NOT NULL
);
CREATE TABLE categories (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,
  slug TEXT UNIQUE NOT NULL,
  description TEXT,
  parent_id INTEGER,
  
  image TEXT,
  icon TEXT,
  sort_order INTEGER DEFAULT 0,
  status TEXT DEFAULT 'active' CHECK(status IN ('active', 'inactive')),
  
  created_at INTEGER NOT NULL,
  updated_at INTEGER NOT NULL,
  
  FOREIGN KEY (parent_id) REFERENCES categories(id) ON DELETE SET NULL
);
CREATE TABLE video_syncs (id INTEGER PRIMARY KEY AUTOINCREMENT, tiktok_url TEXT NOT NULL, tiktok_video_id TEXT, r2_path TEXT, r2_url TEXT, video_duration INTEGER, file_size INTEGER, brand_voice TEXT DEFAULT 'friendly', status TEXT DEFAULT 'pending', error_message TEXT, created_by INTEGER, created_at INTEGER NOT NULL, updated_at INTEGER NOT NULL);
CREATE TABLE ai_generated_content (id INTEGER PRIMARY KEY AUTOINCREMENT, video_sync_id INTEGER NOT NULL, version INTEGER DEFAULT 1, caption TEXT, hashtags TEXT, video_analysis TEXT, is_selected INTEGER DEFAULT 0, is_edited INTEGER DEFAULT 0, created_at INTEGER NOT NULL, FOREIGN KEY (video_sync_id) REFERENCES video_syncs(id) ON DELETE CASCADE);
CREATE TABLE facebook_posts (id INTEGER PRIMARY KEY AUTOINCREMENT, video_sync_id INTEGER NOT NULL, ai_content_id INTEGER, page_id TEXT NOT NULL, post_id TEXT, post_url TEXT, status TEXT DEFAULT 'pending', error_message TEXT, views INTEGER DEFAULT 0, likes INTEGER DEFAULT 0, comments INTEGER DEFAULT 0, shares INTEGER DEFAULT 0, published_at INTEGER, created_at INTEGER NOT NULL, FOREIGN KEY (video_sync_id) REFERENCES video_syncs(id) ON DELETE CASCADE, FOREIGN KEY (ai_content_id) REFERENCES ai_generated_content(id));
CREATE TABLE fanpages (   id INTEGER PRIMARY KEY AUTOINCREMENT,   page_id TEXT UNIQUE NOT NULL,   page_name TEXT NOT NULL,   access_token TEXT,   auto_reply_enabled INTEGER DEFAULT 0,   auto_hide_phone INTEGER DEFAULT 0,   reply_template TEXT DEFAULT 'Shop Ä‘Ã£ inbox báº¡n rá»“i áº¡, báº¡n check tin nháº¯n chá» nhÃ©! â¤ï¸',   website_link TEXT DEFAULT 'https://shophuyvan.vn',   is_active INTEGER DEFAULT 1,   is_default INTEGER DEFAULT 0,   token_expires_at INTEGER,   total_posts INTEGER DEFAULT 0,   total_ads INTEGER DEFAULT 0,   last_post_at INTEGER,   created_at INTEGER NOT NULL,   updated_at INTEGER NOT NULL );
CREATE TABLE automation_jobs (   id INTEGER PRIMARY KEY AUTOINCREMENT,   product_id TEXT NOT NULL,   product_name TEXT,   product_slug TEXT,   product_url TEXT,   product_price REAL,   product_image TEXT,   tiktok_url TEXT,   tiktok_video_id TEXT,   video_r2_path TEXT,   video_r2_url TEXT,   video_file_size INTEGER,   video_duration INTEGER,   status TEXT DEFAULT 'draft' CHECK(status IN (     'draft', 'video_uploaded', 'ai_generated', 'assigned',     'publishing', 'published', 'completed', 'failed'   )),   current_step INTEGER DEFAULT 1,   total_variants INTEGER DEFAULT 0,   total_fanpages_assigned INTEGER DEFAULT 0,   total_posts_published INTEGER DEFAULT 0,   total_posts_failed INTEGER DEFAULT 0,   campaign_id TEXT,   campaign_name TEXT,   error_message TEXT,   error_step INTEGER,   created_by INTEGER,   created_at INTEGER NOT NULL,   updated_at INTEGER NOT NULL,   completed_at INTEGER );
CREATE TABLE content_variants (   id INTEGER PRIMARY KEY AUTOINCREMENT,   job_id INTEGER NOT NULL,   version INTEGER NOT NULL CHECK(version BETWEEN 1 AND 5),   tone TEXT NOT NULL CHECK(tone IN (     'casual', 'sale-heavy', 'storytelling', 'professional', 'tips'   )),   caption TEXT NOT NULL,   hashtags TEXT,   cta TEXT,   is_edited INTEGER DEFAULT 0,   original_caption TEXT,   created_at INTEGER NOT NULL,   FOREIGN KEY (job_id) REFERENCES automation_jobs(id) ON DELETE CASCADE );
CREATE TABLE fanpage_assignments (   id INTEGER PRIMARY KEY AUTOINCREMENT,   job_id INTEGER NOT NULL,   fanpage_id TEXT NOT NULL,   fanpage_name TEXT,   variant_id INTEGER NOT NULL,   status TEXT DEFAULT 'pending' CHECK(status IN (     'pending', 'publishing', 'published', 'failed'   )),   post_id TEXT,   post_url TEXT,   error_message TEXT,   retry_count INTEGER DEFAULT 0,   created_at INTEGER NOT NULL,   updated_at INTEGER NOT NULL,   published_at INTEGER, scheduled_time INTEGER DEFAULT NULL,   FOREIGN KEY (job_id) REFERENCES automation_jobs(id) ON DELETE CASCADE,   FOREIGN KEY (variant_id) REFERENCES content_variants(id) ON DELETE CASCADE );
CREATE TABLE job_campaigns (   id INTEGER PRIMARY KEY AUTOINCREMENT,   job_id INTEGER NOT NULL,   fb_campaign_id TEXT,   fb_adset_id TEXT,   campaign_name TEXT NOT NULL,   daily_budget INTEGER NOT NULL,   target_url TEXT,   targeting_config TEXT,   status TEXT DEFAULT 'draft' CHECK(status IN (     'draft', 'creating', 'active', 'paused', 'failed'   )),   total_ads INTEGER DEFAULT 0,   ads_data TEXT,   error_message TEXT,   created_at INTEGER NOT NULL,   updated_at INTEGER NOT NULL,   FOREIGN KEY (job_id) REFERENCES automation_jobs(id) ON DELETE CASCADE );
CREATE TABLE product_viral_videos (   id INTEGER PRIMARY KEY AUTOINCREMENT,   product_id TEXT NOT NULL,   job_id INTEGER,   tiktok_url TEXT NOT NULL,   tiktok_video_id TEXT,   views INTEGER DEFAULT 0,   likes INTEGER DEFAULT 0,   shares INTEGER DEFAULT 0,   comments INTEGER DEFAULT 0,   viral_score INTEGER DEFAULT 0,   is_used INTEGER DEFAULT 0,   used_at INTEGER,   created_at INTEGER NOT NULL,   FOREIGN KEY (job_id) REFERENCES automation_jobs(id) ON DELETE SET NULL );
CREATE TABLE facebook_groups (   id INTEGER PRIMARY KEY AUTOINCREMENT,   group_id TEXT UNIQUE NOT NULL,   group_name TEXT NOT NULL,   privacy TEXT,   admin_user_id INTEGER,   can_post INTEGER DEFAULT 1,   is_active INTEGER DEFAULT 1,   auto_share_enabled INTEGER DEFAULT 0,   created_at INTEGER NOT NULL,   updated_at INTEGER NOT NULL );
CREATE TABLE group_shares (   id INTEGER PRIMARY KEY AUTOINCREMENT,   assignment_id INTEGER NOT NULL,   group_id TEXT NOT NULL,   group_name TEXT,   status TEXT DEFAULT 'pending' CHECK(status IN ('pending', 'shared', 'failed')),   share_post_id TEXT,   share_url TEXT,   error_message TEXT,   created_at INTEGER NOT NULL,   shared_at INTEGER,   FOREIGN KEY (assignment_id) REFERENCES fanpage_assignments(id) ON DELETE CASCADE );
CREATE TABLE settings (   id INTEGER PRIMARY KEY AUTOINCREMENT,   key_name TEXT UNIQUE NOT NULL,   value_json TEXT,   description TEXT,   updated_at INTEGER NOT NULL );
CREATE TABLE douyin_videos (   id TEXT PRIMARY KEY,   product_id INTEGER,   douyin_url TEXT NOT NULL,   original_video_url TEXT,   original_cover_url TEXT,   duration INTEGER DEFAULT 0,   original_script_cn TEXT,   ai_analysis_json TEXT,   vietnamese_scripts_json TEXT,   selected_script_version INTEGER DEFAULT 1,   final_script_text TEXT,   voice_model TEXT DEFAULT 'vi-VN-Standard-A',   voice_speed REAL DEFAULT 1.0,   voice_pitch REAL DEFAULT 0.0,   background_music_mode TEXT DEFAULT 'keep_original',   background_music_url TEXT,   status TEXT DEFAULT 'pending',   progress INTEGER DEFAULT 0,   current_step TEXT,   vietnamese_audio_url TEXT,   final_video_url TEXT,   error_message TEXT,   created_at INTEGER NOT NULL,   updated_at INTEGER NOT NULL );
CREATE TABLE douyin_queue_jobs (   id INTEGER PRIMARY KEY AUTOINCREMENT,   video_id TEXT NOT NULL,   action TEXT NOT NULL,   status TEXT DEFAULT 'pending',   attempts INTEGER DEFAULT 0,   last_attempt_at INTEGER,   payload TEXT,   created_at INTEGER NOT NULL );
